import { useState } from 'react'

export const JoinSubmit = () => {
    const [id, setId] = useState(0)
    const [pw, setPw] = useState("")
    const [Validpw, setValidPw] = useState(0)
    const [Checkpw, setCheckPw] = useState(false)
    const [showpw, setShowpw] = useState(false)
    const [email, setEmail] = useState("")

    const regId = /^[A-Za-z0-9]*$/; 
    const regPw = /^[A-Za-z0-9]+$/; 

    const idChange: React.ChangeEventHandler<HTMLInputElement> = (e) => {
        if (e.target.value === "") {
            setId(0)
        }
        else if (!regId.test(e.target.value)) {
            setId(1)
        }
        else if (e.target.value.length < 5){
            setId(2)
        }
        else {
            setId(3)
        }
    }

    const pwChange: React.ChangeEventHandler<HTMLInputElement> = (e) => {
        setPw(e.target.value)

        if (e.target.value === "") {
            setValidPw(0)
        }
        else if (!regPw.test(e.target.value)) {
            setValidPw(1)
        }
        else if (e.target.value.length < 8) {
            setValidPw(2)
        }
        else {
            setValidPw(3)
        }
    }

    const pwCheckChange: React.ChangeEventHandler<HTMLInputElement> = (e) => {
        if (e.target.value === pw) {
            setCheckPw(true)
        }
        else {
            setCheckPw(false)
        }
    }

    const emailChange: React.ChangeEventHandler<HTMLInputElement> = (e) => {
        setEmail(e.target.value)
    }

    const showPw: React.ChangeEventHandler<HTMLInputElement> = (e) => {
        setShowpw(!showpw)
    }

    return (
        <div>
            <form action='./join' method='post'>
                <div className='inputbox'>
                    <label htmlFor="id">아이디</label>
                    <input onChange={idChange} id='id' required autoFocus></input>
                    <p className='idinputbox'>{id == 0 ? "아이디를 입력해 주세요!" : id == 1 ? "아이디는 영어만 가능합니다" : id === 2 ? "아이디는 5글자 이상이어야 합니다." : "맞는 아이디입니다."}</p>
                </div>
                <div className='inputbox'>
                    <label htmlFor="pw">비밀번호</label>
                    <input type='password' onChange={pwChange} id='pw' required autoFocus></input> 
                    <p className='pwinptbox'>{pw && showpw ? pw : ""}</p>
                    <p>비밀번호 보기<input type='checkbox' onChange={showPw}></input></p>                    
                </div>
                <div className='inputbox'>
                    <label htmlFor="pwCheck">비밀번호 확인</label>
                    <input type='password' onChange={pwCheckChange} id='pwCheck' required autoFocus></input>
                    <p>{!Checkpw ? "비밀번호가 일치하지 않습니다." : "비밀번호 일치"}</p>
                </div>
                <div className='inputbox'>
                    <label htmlFor="email">이메일</label>
                    <input onChange={emailChange} id='email' required autoFocus></input>
                    <div>{email ? email : "None"}</div>
                </div>
                <button type='submit'>회원가입</button>
            </form>
        </div>
    )
}
